import React, { useState, useEffect } from "react";
import styled from "styled-components";
import ThumbnailBlog from "../04 Organisms/BlogPage/ThumbnailBlog";
import { fonts } from "../01 Atoms/globalStyle";
import { media } from "../01 Atoms/MediaQueries";
import { colors, colorsRoles } from "../01 Atoms/Colors";
import { API_URL } from "../../config";
import parse from "html-react-parser";
import {pathblog} from "../01 Atoms/Data";
import { useParams } from "react-router-dom";

const BlogPage = () => {

  const {slug} = useParams()

  const [isLoading, setIsLoading] = useState(true);
  const [postItem, setPostItem] = useState(null);

  useEffect(() => {
    fetch(API_URL + "/blog-posts/", {
      method: "GET",
      headers: {
        Accept: "Application/json",
      },
    })
      .then((res) => res.json())
      .then((response) => {
        setTimeout(() => {
          setPostItem(response);
          setIsLoading(false);
        }, 0);
      });
  }, []);

  // var parse = require("html-react-parser");

  return (
    <>
      <BlogPageContainer>
        <h1>Blog</h1>

        {isLoading
          ? "Loading..."
          : postItem.map((item, index) => (
              <ThumbnailBlog
                key={index}
                Cover={item.cover.url}
                MainTitlePost={item.MainTitlePost}
                content={parse(item.content)}
                slug={pathblog+"/"+ item.slug}
              />
            ))}
      </BlogPageContainer>
    </>
  );
};

export default BlogPage;

export const BlogPageContainer = styled.div`
  width: 100%;
  height: 100%;
  background-color: ${colors.White};
  padding: 64px 16px 0px 16px;
  display: flex;
  align-items: center;
  flex-direction: column;

  ${media.desktop`
width: calc(100% - 80px);
`}

  & h1 {
    position: relative;
    ${fonts.PlayFairDisplay};
    font-size: 4rem;
    line-height: 48px;
    color: ${colorsRoles.DarkGrey};
    ${media.desktop`
    font-size: 6rem;
    line-height: 84px;
    flex-direction: unset;
    align-items: unset;
  `}
  }
`;
